					<div id="main" className="jumbotron text-start" style={{justifyContent: 'center', alignItems: 'center', width: '75%', marginLeft: 'auto', marginRight: 'auto', backgroundColor: '#DBEEF4',   borderRadius: '5px',
					  boxShadow: '10px 10px 10px grey'}}>

						<p>Enter your car's model year</p>

						<input id="yearInput" type="text" style={{width: '50%'}} onChange={this.saveState}></input>

					<br/>
					<br/>

					<button id="submitButton" className="btn btn-primary btn-lg text-center" onClick={this.getMakes} disabled={!isEnabled}>Go</button>
					</div>
				</div>
			</div>

		);
	}
}

export default HomePage;
