import React from 'react';
import { Link } from 'react-router-dom';
import LogoImg from './NoImage.jpeg';

function changeBackground(color) {
	document.body.style.background = color;
}

class Home2 extends React.Component {
	constructor(props) {
	 super(props);

	 this.state = {
			mileage: 0,
			year: 0,
			make: "",
			//trim: "trimChoice",
			//models: [<option key={0} value="modelChoice">Choose a Model</option>],
			//years: [<option key={0} value="yearChoice">Choose a Year</option>],
			//trims: [<option key={0} value="trimChoice">Choose a Trim Level</option>]
	 };

	 //this.addOne = this.addOne.bind(this)
	 //this.getBeginning = this.getBeginning.bind(this);
	 //this.saveState = this.saveState.bind(this);
	 this.routeChange = this.routeChange.bind(this);
	 this.routeChangeBack = this.routeChangeBack.bind(this);
	 this.getMakes = this.getMakes.bind(this);
	 this.setState = this.setState.bind(this);
	 //console.log(this.state);
	 //console.log(this);
	}

	componentDidMount() {
    console.log("I got here");
		var button = document.getElementById("nextButton");
		button.setAttribute("disabled","true");
    // set el height and width etc.
  }

	routeChangeBack(){
 		let path = "../home";
		this.setState({
			models: 0,
			years: 0,
			trims: 0
		}, () => this.props.history.push({
  		pathname: '../home',
  		state: this.state
		}))
 	}

	routeChange(){
		let path = "../home3";
		this.setState({
			models: 0,
			years: 0,
			trims: 0
		}, () => this.props.history.push({
			pathname: '../home3',
			state: this.state
		}))
	}

	displaySelected() {
		var makes = document.getElementsByClassName("vertical");

		for (var i = 0; i < makes.length; i++) {
			var element = document.getElementById(i.toString());
			var make = element.children[1].innerHTML;
			//console.log(make);
			//console.log(this.state.make)
			if (make === this.state.make) {
				console.log("Yays");
				element.setAttribute("class","verticalSelected");
				console.log(document.getElementById(i.toString()));

				this.state.done = "true";

				break;
			}
		}
	}

	getMakes() {
		var url = "https://www.fueleconomy.gov/ws/rest/vehicle/menu/make?year=";

		var yearNumber = this.props.location.state.year;

		url = url + yearNumber;

		//console.log(url);

		var that = this;

		fetch(url)
		.then(function(response) {
			return response.text();
		})
		.then(function(text) {
			//console.log(that);
			const parser = new DOMParser();
			const xml = parser.parseFromString(text, 'text/xml');

			var elements = xml.getElementsByTagName("text");
			var length = elements.length;

			var number = document.createElement("p");
			number.setAttribute("id", "length");
			number.setAttribute("text", length.toString());

			//console.log(document.getElementById("length").value);

			var backButton = <button class="btn btn-primary btn-lg" style="margin-left:40; margin-right:40; margin-bottom:30px; margin-top:5px; float:left; width:100px" onclick="getBeginning()">Back</button>;

			document.getElementById("main2").innerHTML = xml.getElementsByTagName("text")[1].childNodes[0].nodeValue;

			var newHTML = "";
			newHTML += "<div id=\"grids\" class=\"portfolio\">";
			/*for (var i = 0; i < length; i++) {
				newHTML += "<div id=\"" + i + "\"class=\"vertical\" onclick=\"click()\">";
				<script>
				function click() {

				}
				newHTML += "<img src=" + LogoImg + " class=\"image\" />"
				newHTML += "<p>" + elements[i].childNodes[0].nodeValue + "</p>";
				newHTML += "</div>";
			}*/
			newHTML += "</div>";
			newHTML += "<br/><br/>";
			//newHTML += "{backButton}";
			//newHTML += "<button class=\"btn btn-primary btn-lg\" style=\"margin-right:40; margin-left:40; margin-bottom:30px; margin-top:5px; float:right; width:100px\" disabled> Next</button>";
			//newHTML += "<br/>";
			/*newHTML += "<script>";
			newHTML += "function getBeginning() {";
			newHTML += "var new = \"\";";

			newHTML += "new += \"<p>Enter your car's model year</p>\";";

			newHTML += "new += \"<input id='yearInput' type='text' style='width: '50%'' onChange={this.saveState}></input>\";";
			newHTML += "new += \"<br/>\";";
			newHTML += "new += \"<br/>\";";

			newHTML += "new += \"<button id='submitButton' class='btn btn-primary btn-lg text-center' onClick={this.getMakes} disabled={!isEnabled}>Go</button>\";";
			newHTML += "new += \"<br/>\"";

			newHTML += "document.getElementById(\"main\").innerHTML = new;"
		newHTML += "}";
		newHTML += "</script>";*/
			//newHTML += "<img src=" + LogoImg + " className=\"center\" alt=\"Logo\" style={{height: 225, width: 300,  marginBottom: 20, marginTop: 2}}/>";


				//console.log(newHTML);

				document.getElementById("main2").innerHTML = newHTML;
			//});

			for (var i = 0; i < length; i++) {
				var div = document.createElement("div");
				div.setAttribute("class", "vertical");
				div.setAttribute("id", i.toString());
				div.onclick = function() {
					var id = this.getAttribute("id");

					var makes = document.getElementsByClassName("vertical");

					for (var i = 0; i < makes.length; i++) {
						document.getElementById(i.toString()).setAttribute("style","");
					}

					this.setAttribute("style", "background-color: #007bff; color: white; border-radius: 7px");

					that.state.make = this.children[1].innerHTML;


					var nextButton = document.getElementById("nextButton");
					nextButton.disabled = false;
				}

				newHTML = "";
				newHTML += "<img src=" + LogoImg + " class=\"image\" />"
				newHTML += "<p>" + elements[i].childNodes[0].nodeValue + "</p>";
				newHTML += "</div>";

				div.innerHTML = newHTML;

				document.getElementById("grids").appendChild(div);

			}

		})
		.catch(err => {
			return;
		})
	}

	changeItem() {
		var input = document.getElementById('mileageInput');
		console.log(input.value);
	}


	render() {
		changeBackground("#D3D3D3");

		this.getMakes();

		const isEnabled = (this.state.make !== "")

		return (
			<div className="container-fluid" style={{backgroundColor: "#D3D3D3", overflow: "scroll", width: '100%', padding: 0, margin: 0, height: '100%'}}>
				<div className="text-center" style={{backgroundColor: "white", width: '100%', height: '100%', padding: 0, margin: 0}}>
					<h1>My Car Maintenance</h1>
					<hr width="50%">
					</hr>
					<br></br>
					<Link to="about" className="btn btn-primary btn-lg">About Us</Link>
					<br/><br/>
					<br/>
				</div>

				<br></br>

				<div className="text-center" style={{backgroundColor: "#D3D3D3", width: '100%', height: '100%', padding: 0, margin: 0}}>
					<h2> Enter Your Information To Get Maintenance Recommendations</h2>
					<div id="main" className="jumbotron text-start" style={{justifyContent: 'center', alignItems: 'center', width: '75%', marginLeft: 'auto', marginRight: 'auto', backgroundColor: '#DBEEF4',   borderRadius: '5px',
					  boxShadow: '10px 10px 10px grey'}}>

						<div id="main2">
						</div>

						<button className="btn btn-primary btn-lg" style={{marginLeft:40, marginRight:40, marginBottom:"30px", marginTop:"5px", float:"left", width:"100px"}} onClick={this.routeChangeBack}>Back</button>
						<button id="nextButton" className="btn btn-primary btn-lg" style={{marginRight:40, marginLeft:40, marginBottom:"30px", marginTop:"5px", float:"right", width:"100px"}} onClick={this.routeChange}> Next</button>

						<br/>
					</div>
				</div>
			</div>

		);
	}
}

export default Home2;
