	 this.routeChangeBack = this.routeChangeBack.bind(this);
	 this.getMakes = this.getMakes.bind(this);
	 this.setState = this.setState.bind(this);
	 this.displaySelected = this.displaySelected.bind(this);
	 console.log(this.state);
	 console.log(this);
	}

	routeChangeBack(){
 		let path = "../home";
		this.setState({
			models: 0,
			years: 0,
			trims: 0
		}, () => this.props.history.push({
  		pathname: '../home',
  		state: this.state
		}))
 	}

	routeChange(){
		let path = "../home";
		this.setState({
			models: 0,
			years: 0,
			trims: 0
		}, () => this.props.history.push({
			pathname: '../home',
			state: this.state
		}))
	}

	displaySelected() {
		var makes = document.getElementsByClassName("vertical");

		console.log("three");
		console.log(makes);

		for (var i = 0; i < makes.length; i++) {
			document.getElementById(i.toString()).setAttribute("style","");
			console.log(i);
		}
	}

