		var url = "https://www.fueleconomy.gov/ws/rest/vehicle/menu/make?year=";

		var yearNumber = this.props.location.state.year;

		url = url + yearNumber;

		console.log(url);

		var that = this;

		fetch(url)
		.then(function(response) {
			return response.text();
		})
		.then(function(text) {
			console.log(that);
			const parser = new DOMParser();
			const xml = parser.parseFromString(text, 'text/xml');

			var elements = xml.getElementsByTagName("text");
			var length = elements.length;

			var number = document.createElement("p");
			number.setAttribute("id", "length");
			number.setAttribute("text", length.toString());

			//console.log(document.getElementById("length").value);

			var backButton = <button class="btn btn-primary btn-lg" style="margin-left:40; margin-right:40; margin-bottom:30px; margin-top:5px; float:left; width:100px" onclick="getBeginning()">Back</button>;

			document.getElementById("main2").innerHTML = xml.getElementsByTagName("text")[1].childNodes[0].nodeValue;

			var newHTML = "";
			newHTML += "<div id=\"grids\" class=\"portfolio\">";
			/*for (var i = 0; i < length; i++) {
				newHTML += "<div id=\"" + i + "\"class=\"vertical\" onclick=\"click()\">";
				<script>
				function click() {

				}
				newHTML += "<img src=" + LogoImg + " class=\"image\" />"
				newHTML += "<p>" + elements[i].childNodes[0].nodeValue + "</p>";
				newHTML += "</div>";
			}*/
			newHTML += "</div>";
			newHTML += "<br/><br/>";
			//newHTML += "{backButton}";
			//newHTML += "<button class=\"btn btn-primary btn-lg\" style=\"margin-right:40; margin-left:40; margin-bottom:30px; margin-top:5px; float:right; width:100px\" disabled> Next</button>";
			//newHTML += "<br/>";
			/*newHTML += "<script>";
			newHTML += "function getBeginning() {";
			newHTML += "var new = \"\";";

			newHTML += "new += \"<p>Enter your car's model year</p>\";";

			newHTML += "new += \"<input id='yearInput' type='text' style='width: '50%'' onChange={this.saveState}></input>\";";
			newHTML += "new += \"<br/>\";";
			newHTML += "new += \"<br/>\";";

			newHTML += "new += \"<button id='submitButton' class='btn btn-primary btn-lg text-center' onClick={this.getMakes} disabled={!isEnabled}>Go</button>\";";
			newHTML += "new += \"<br/>\"";

			newHTML += "document.getElementById(\"main\").innerHTML = new;"
		newHTML += "}";
		newHTML += "</script>";*/
			//newHTML += "<img src=" + LogoImg + " className=\"center\" alt=\"Logo\" style={{height: 225, width: 300,  marginBottom: 20, marginTop: 2}}/>";


				console.log(newHTML);

				document.getElementById("main2").innerHTML = newHTML;
			//});

			for (var i = 0; i < length; i++) {
				var div = document.createElement("div");
				div.setAttribute("class", "vertical");
				div.setAttribute("id", i.toString());
				div.onclick = function() {
					var id = this.getAttribute("id");
					console.log(id);

					var makes = document.getElementsByClassName("vertical");

					for (var i = 0; i < makes.length; i++) {
						document.getElementById(i.toString()).setAttribute("style","");
					}

					this.setAttribute("style", "background-color: #007bff; color: white; border-radius: 7px");

